import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { Brain, Sparkles, MessageCircle, Zap, Users, Shield, ArrowRight, CheckCircle, Star } from 'lucide-react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const LandingPage: React.FC = () => {
  const navigate = useNavigate();

  const features = [
    {
      icon: <Brain className="w-6 h-6" />,
      title: "Advanced Reasoning",
      description: "Deep understanding and contextual awareness that adapts to your thinking patterns"
    },
    {
      icon: <Sparkles className="w-6 h-6" />,
      title: "Creative Intelligence",
      description: "Breakthrough ideas and innovative solutions powered by cutting-edge AI"
    },
    {
      icon: <MessageCircle className="w-6 h-6" />,
      title: "Natural Dialogue",
      description: "Seamless conversations that feel intuitive and genuinely helpful"
    },
    {
      icon: <Zap className="w-6 h-6" />,
      title: "Instant Processing",
      description: "Lightning-fast responses without compromising on quality or depth"
    }
  ];

  const steps = [
    {
      number: "01",
      title: "Connect",
      description: "Start a conversation with our intelligent AI assistant"
    },
    {
      number: "02",
      title: "Collaborate",
      description: "Work together on complex problems and creative challenges"
    },
    {
      number: "03",
      title: "Create",
      description: "Transform ideas into reality with AI-powered insights"
    }
  ];

  const testimonials = [
    {
      name: "Sarah Chen",
      role: "Product Designer",
      company: "Figma",
      content: "Soul has revolutionized my design process. It's like having a brilliant design partner who never sleeps.",
      rating: 5
    },
    {
      name: "Marcus Rodriguez",
      role: "Engineering Lead",
      company: "Stripe",
      content: "The depth of technical understanding is remarkable. Soul helps me solve complex architectural challenges.",
      rating: 5
    },
    {
      name: "Emily Watson",
      role: "Content Strategist",
      company: "Notion",
      content: "Soul doesn't just generate content—it helps me think strategically about messaging and narrative.",
      rating: 5
    }
  ];

  return (
    <div className="min-h-screen relative overflow-hidden bg-white">
      <Header />

      {/* Animated AI Background */}
      <div className="smoke-background">
        <div className="smoke-container">
          <div className="smoke-particle"></div>
          <div className="smoke-particle"></div>
          <div className="smoke-particle"></div>
          <div className="smoke-wisp"></div>
          <div className="smoke-wisp"></div>
          <div className="smoke-trail"></div>
          <div className="smoke-trail"></div>
        </div>
      </div>

      {/* Hero Section */}
      <section className="relative pt-24 pb-32 px-6 overflow-hidden">
        <div className="max-w-7xl mx-auto">
          <div className="text-center max-w-4xl mx-auto">
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, ease: "easeOut" }}
              className="mb-8"
            >
              <span className="inline-flex items-center px-4 py-2 rounded-full bg-gray-50 border border-gray-200 text-sm font-medium text-gray-700 mb-8">
                <Sparkles className="w-4 h-4 mr-2 text-gray-500" />
                Your Intelligent Partner in Code, Thought, and Creation
              </span>
            </motion.div>

            <motion.h1 
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.1, ease: "easeOut" }}
              className="text-5xl md:text-7xl lg:text-8xl font-bold mb-8 tracking-tight text-black leading-[0.9]"
            >
              AI with
              <span className="block bg-gradient-to-r from-gray-900 to-gray-600 bg-clip-text text-transparent">
                Soul
              </span>
            </motion.h1>

            <motion.p 
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2, ease: "easeOut" }}
              className="text-xl md:text-2xl text-gray-600 mb-12 max-w-3xl mx-auto leading-relaxed font-light"
            >
              Experience the future of intelligent assistance. Thoughtful, creative, and remarkably human.
            </motion.p>

            <motion.div 
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.3, ease: "easeOut" }}
              className="flex flex-col sm:flex-row gap-4 justify-center items-center"
            >
              <button 
                onClick={() => navigate('/onboarding')}
                className="group px-8 py-4 bg-black text-white rounded-xl font-semibold text-lg hover:bg-gray-800 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 flex items-center gap-2"
              >
                Start Building
                <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-200" />
              </button>
              <button 
                onClick={() => navigate('/about')}
                className="px-8 py-4 bg-transparent border border-gray-300 text-gray-700 rounded-xl font-semibold text-lg hover:border-gray-400 hover:bg-gray-50 transition-all duration-300"
              >
                Watch Demo
              </button>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4, ease: "easeOut" }}
              className="mt-16 flex items-center justify-center gap-8 text-sm text-gray-500"
            >
              <div className="flex items-center gap-2">
                <CheckCircle className="w-4 h-4 text-green-500" />
                No credit card required
              </div>
              <div className="flex items-center gap-2">
                <CheckCircle className="w-4 h-4 text-green-500" />
                Free to start
              </div>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-20 px-4 relative z-10">
        <div className="max-w-6xl mx-auto bg-transparent rounded-xl p-8">
          <motion.h2 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-4xl md:text-5xl font-bold text-center mb-16 text-black"
          >
            Features
          </motion.h2>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {features.map((feature, index) => (
                <motion.div 
                  key={index}
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  className="p-6 bg-transparent rounded-xl border border-gray-300 hover:border-black transition-all duration-300 h-full"
                >
                  <div className="text-black mb-4">{feature.icon}</div>
                  <h3 className="text-xl font-semibold mb-3 text-black">{feature.title}</h3>
                <p className="text-black">{feature.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* How it Works Section */}
      <section className="py-20 px-4 relative z-10">
        <div className="max-w-6xl mx-auto bg-transparent rounded-xl p-8">
          <motion.h2 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-4xl md:text-5xl font-bold text-center mb-16 text-black"
          >
            How it Works
          </motion.h2>

          <div className="grid md:grid-cols-3 gap-12">
            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              className="text-center"
            >
              <div className="w-16 h-16 bg-black text-white rounded-full flex items-center justify-center text-2xl font-bold mx-auto mb-6">1</div>
              <h3 className="text-xl font-semibold mb-4 text-black">Start Chatting</h3>
              <p className="text-black">Simply type your question or request in natural language</p>
            </motion.div>

            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.2 }}
              className="text-center"
            >
              <div className="w-16 h-16 bg-black text-white rounded-full flex items-center justify-center text-2xl font-bold mx-auto mb-6">2</div>
              <h3 className="text-xl font-semibold mb-4 text-black">AI Understands</h3>
              <p className="text-black">Soul processes your request with deep understanding and context</p>
            </motion.div>

            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
              className="text-center"
            >
              <div className="w-16 h-16 bg-black text-white rounded-full flex items-center justify-center text-2xl font-bold mx-auto mb-6">3</div>
              <h3 className="text-xl font-semibold mb-4 text-black">Get Results</h3>
              <p className="text-black">Receive thoughtful, accurate responses tailored to your needs</p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Testimonials Section */}
      <section className="py-20 px-4 relative z-10">
        <div className="max-w-6xl mx-auto bg-transparent rounded-xl p-8">
          <motion.h2 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-4xl md:text-5xl font-bold text-center mb-16 text-black"
          >
            Testimonials
          </motion.h2>

          <div className="grid md:grid-cols-3 gap-8">
            {testimonials.map((testimonial, index) => (
              <motion.div 
                key={index}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                className="bg-transparent p-8 rounded-xl border border-gray-300 transition-all duration-200"
              >
                <p className="text-black mb-6 italic">"{testimonial.content}"</p>
                <div>
                  <p className="font-semibold text-black">{testimonial.name}</p>
                  <p className="text-sm text-black">{testimonial.role}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 px-4 relative z-10">
        <div className="max-w-4xl mx-auto text-center bg-transparent rounded-xl p-8">
          <motion.h2 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-4xl md:text-5xl font-bold mb-8 text-black"
          >
            Ready to Experience AI with Soul?
          </motion.h2>

          <motion.p
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            className="text-xl mb-12 max-w-2xl mx-auto text-black"
          >
            Join thousands of users who have transformed their workflow with our intelligent AI companion.
          </motion.p>

          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
          >
            <button 
              onClick={() => navigate('/onboarding')}
              className="px-8 py-4 bg-black text-white rounded-lg font-semibold text-lg hover:bg-gray-800 transition-colors duration-200 shadow-lg"
            >
              Get Started Now
            </button>
          </motion.div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default LandingPage;
