import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { useEffect, useRef, useState } from 'react';
import { ArrowRight } from 'lucide-react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const LandingPage: React.FC = () => {
  const navigate = useNavigate();
  const videoRef = useRef<HTMLVideoElement>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const [hasPlayedAudio, setHasPlayedAudio] = useState(false);
  const [userInteracted, setUserInteracted] = useState(false);

  // Initialize audio for one-time playback
  useEffect(() => {
    // Create audio element for ambient sound
    audioRef.current = new Audio('/ambient-sound.mp3'); // You'll need to add this file
    audioRef.current.volume = 0.3; // Subtle volume
    audioRef.current.preload = 'auto';

    return () => {
      if (audioRef.current) {
        audioRef.current.pause();
        audioRef.current = null;
      }
    };
  }, []);

  // Handle user interaction for audio playback
  useEffect(() => {
    const handleUserInteraction = () => {
      if (!hasPlayedAudio && audioRef.current && userInteracted) {
        audioRef.current.play().then(() => {
          setHasPlayedAudio(true);
        }).catch((error) => {
          console.log('Audio playback failed:', error);
        });
      }
    };

    const enableInteraction = () => {
      setUserInteracted(true);
    };

    // Listen for user interactions
    document.addEventListener('click', enableInteraction, { once: true });
    document.addEventListener('touchstart', enableInteraction, { once: true });
    document.addEventListener('keydown', enableInteraction, { once: true });

    if (userInteracted) {
      handleUserInteraction();
    }

    return () => {
      document.removeEventListener('click', enableInteraction);
      document.removeEventListener('touchstart', enableInteraction);
      document.removeEventListener('keydown', enableInteraction);
    };
  }, [hasPlayedAudio, userInteracted]);

  // Enhanced video control with mobile compatibility
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const playVideo = async () => {
      try {
        // Always start muted for autoplay compatibility
        video.muted = true;
        await video.play();

        // Try to play audio once on first load if user has interacted
        if (!hasPlayedAudio && userInteracted && audioRef.current) {
          try {
            await audioRef.current.play();
            setHasPlayedAudio(true);
          } catch (audioError) {
            console.log('Audio autoplay blocked:', audioError);
          }
        }
      } catch (error) {
        console.log('Video autoplay failed:', error);
        // Fallback: ensure video is muted for compatibility
        video.muted = true;
      }
    };

    const handleCanPlay = () => {
      playVideo();
    };

    const handleLoadedData = () => {
      playVideo();
    };

    // Mobile-specific handling
    const handleLoadStart = () => {
      // Ensure video is properly configured for mobile
      video.setAttribute('playsinline', 'true');
      video.setAttribute('webkit-playsinline', 'true');
    };

    // Add event listeners for video loading
    video.addEventListener('canplay', handleCanPlay);
    video.addEventListener('loadeddata', handleLoadedData);
    video.addEventListener('loadstart', handleLoadStart);

    // Try to play immediately if video is already loaded
    if (video.readyState >= 3) {
      playVideo();
    }

    // Cleanup
    return () => {
      video.removeEventListener('canplay', handleCanPlay);
      video.removeEventListener('loadeddata', handleLoadedData);
      video.removeEventListener('loadstart', handleLoadStart);
    };
  }, [hasPlayedAudio, userInteracted]);

  // Handle page visibility changes and mobile lifecycle
  useEffect(() => {
    const handleVisibilityChange = () => {
      const video = videoRef.current;
      if (video && !document.hidden) {
        // Page became visible, ensure video is playing silently
        if (video.paused) {
          video.muted = true; // Always silent after initial audio
          video.play().catch(console.error);
        }
      }
    };

    const handlePageShow = () => {
      const video = videoRef.current;
      if (video) {
        // Force video restart on page show (handles back/forward navigation)
        video.muted = true; // Always silent after initial load
        video.currentTime = 0;
        video.play().catch(console.error);
      }
    };

    // Mobile-specific events
    const handleFocus = () => {
      const video = videoRef.current;
      if (video && video.paused) {
        video.muted = true;
        video.play().catch(console.error);
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    window.addEventListener('pageshow', handlePageShow);
    window.addEventListener('focus', handleFocus);

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
      window.removeEventListener('pageshow', handlePageShow);
      window.removeEventListener('focus', handleFocus);
    };
  }, []);

  // Scroll-reactive parallax effect
  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const scrollPercent = scrollY / (document.documentElement.scrollHeight - window.innerHeight);

      // Update CSS custom property for parallax elements
      document.documentElement.style.setProperty('--scroll-y', `${scrollY * 0.1}px`);

      // Add subtle rotation to floating elements based on scroll
      const floatingElements = document.querySelectorAll('.float-element');
      floatingElements.forEach((element, index) => {
        const rotation = scrollPercent * 360 * (index % 2 === 0 ? 1 : -1);
        (element as HTMLElement).style.transform = `translateY(-20px) rotate(${rotation}deg)`;
      });
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);



  return (
    <div className="min-h-screen relative overflow-hidden">
      <Header />

      {/* Living Canvas Background */}
      <div className="smoke-background">
        <div className="smoke-container">
          {/* Primary Floating Blobs */}
          <div className="smoke-particle parallax-slow"></div>
          <div className="smoke-particle parallax-medium"></div>
          <div className="smoke-particle parallax-fast"></div>
          <div className="smoke-particle parallax-slow"></div>

          {/* Morphing Shapes */}
          <div className="smoke-wisp parallax-medium"></div>
          <div className="smoke-wisp parallax-fast"></div>
          <div className="smoke-wisp parallax-slow"></div>

          {/* Ambient Trails */}
          <div className="smoke-trail parallax-fast"></div>
          <div className="smoke-trail parallax-medium"></div>
          <div className="smoke-trail parallax-slow"></div>
        </div>
      </div>

      {/* Floating UI Elements */}
      <div className="fixed inset-0 pointer-events-none z-10 overflow-hidden">
        <div className="absolute top-20 left-10 w-2 h-2 bg-black/5 dark:bg-white/10 rounded-full float-element"></div>
        <div className="absolute top-40 right-20 w-3 h-3 bg-black/4 dark:bg-white/8 rounded-full float-element"></div>
        <div className="absolute bottom-40 left-20 w-1.5 h-1.5 bg-black/6 dark:bg-white/12 rounded-full float-element"></div>
        <div className="absolute bottom-60 right-10 w-2.5 h-2.5 bg-black/3 dark:bg-white/6 rounded-full float-element"></div>
        <div className="absolute top-1/3 left-1/4 w-1 h-1 bg-black/5 dark:bg-white/10 rounded-full float-element"></div>
        <div className="absolute top-2/3 right-1/3 w-2 h-2 bg-black/4 dark:bg-white/8 rounded-full float-element"></div>
      </div>

      {/* Main Content Section */}
      <section className="relative min-h-screen flex items-center justify-center px-6 overflow-hidden">
        <div className="max-w-4xl mx-auto text-center">
          <motion.h1 
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
            className="text-6xl md:text-8xl lg:text-9xl font-light mb-8 tracking-tight text-black dark:text-white leading-[0.9]"
          >
            What can I help with?
          </motion.h1>

          <motion.div 
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.3, ease: "easeOut" }}
            className="flex justify-center"
          >
            <button 
              onClick={() => navigate('/chat')}
              className="group px-8 py-4 bg-black dark:bg-white text-white dark:text-black rounded-xl font-semibold text-lg hover:bg-gray-800 dark:hover:bg-gray-200 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 flex items-center gap-2"
            >
              Start Chatting
              <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-200" />
            </button>
          </motion.div>
        </div>
      </section>



      <Footer />
    </div>
  );
};

export default LandingPage;
