import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { Brain, Sparkles, MessageCircle, Zap, Users, Shield } from 'lucide-react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const LandingPage: React.FC = () => {
  const navigate = useNavigate();

  const features = [
    {
      icon: <Brain className="w-8 h-8" />,
      title: "Intelligent Conversations",
      description: "Engage with AI that understands context and nuance"
    },
    {
      icon: <Sparkles className="w-8 h-8" />,
      title: "Creative Assistance",
      description: "Unlock your creativity with AI-powered brainstorming"
    },
    {
      icon: <MessageCircle className="w-8 h-8" />,
      title: "Natural Communication",
      description: "Chat naturally with responses that feel human"
    },
    {
      icon: <Zap className="w-8 h-8" />,
      title: "Lightning Fast",
      description: "Get instant responses to your queries and requests"
    },
    {
      icon: <Users className="w-8 h-8" />,
      title: "Personalized Experience",
      description: "AI that adapts to your unique style and preferences"
    },
    {
      icon: <Shield className="w-8 h-8" />,
      title: "Privacy First",
      description: "Your conversations are secure and private"
    }
  ];

  const testimonials = [
    {
      name: "Sarah Chen",
      role: "Creative Director",
      content: "Soul has transformed how I approach creative projects. It's like having a brilliant collaborator available 24/7."
    },
    {
      name: "Marcus Rodriguez",
      role: "Software Engineer",
      content: "The clarity and depth of Soul's responses are remarkable. It's become an essential part of my workflow."
    },
    {
      name: "Emily Watson",
      role: "Writer",
      content: "Soul doesn't just answer questions—it helps me think through complex ideas and find new perspectives."
    }
  ];

  return (
    <div className="min-h-screen relative overflow-hidden">
      <Header />

      {/* Smoke Background */}
      <div className="smoke-background"></div>

      {/* Hero Section */}
      <section className="pt-32 pb-20 px-4 relative overflow-visible" style={{position: 'relative', zIndex: 1}}>
        {/* Smoke source - simulating campfire/cigar */}
        <div className="smoke-source"></div>

        {/* Main smoke effects - rising from bottom like campfire/cigar smoke */}
        <div className="smoke-left" style={{top: '40%'}}></div>
        <div className="smoke-right" style={{top: '40%'}}></div>

        {/* Removed smoke particles/orbs as requested */}

        <div className="max-w-6xl mx-auto text-center relative z-10 text-white">
          <motion.h1 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-6xl md:text-7xl font-bold mb-6 tracking-tight text-black"
          >
            AI with <span className="text-black">Soul</span>
          </motion.h1>

          <motion.p 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            className="text-xl md:text-2xl text-black mb-12 max-w-3xl mx-auto leading-relaxed"
          >
            Your intelligent companion for work, creativity, and clarity.
          </motion.p>

          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            className="flex flex-col sm:flex-row gap-4 justify-center"
          >
            <button 
              onClick={() => navigate('/onboarding')}
              className="px-8 py-4 bg-white text-black rounded-lg font-semibold text-lg hover:bg-gray-200 transition-all duration-200 shadow-lg transform hover:scale-105"
            >
              Try Soul
            </button>
            <button 
              onClick={() => navigate('/about')}
