import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Send, Plus, Settings, MessageCircle, Menu, X } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import PromptDropdown from '../components/PromptDropdown';
import ThemeToggle from '../components/ThemeToggle';

interface Message {
  id: string;
  content: string;
  sender: 'user' | 'ai';
  timestamp: Date;
}

interface Conversation {
  id: string;
  title: string;
  messages: Message[];
  lastUpdated: Date;
}

const ChatInterface: React.FC = () => {
  const navigate = useNavigate();
  const [conversations, setConversations] = useState<Conversation[]>([]);
  const [activeConversationId, setActiveConversationId] = useState<string | null>(null);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [selectedTone, setSelectedTone] = useState('Neutral');
  const [selectedFormat, setSelectedFormat] = useState('Conversational');
  const [selectedCreativity, setSelectedCreativity] = useState('Balanced');
  const [hasStartedChat, setHasStartedChat] = useState(false);

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);

  const activeConversation = conversations.find(conv => conv.id === activeConversationId);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [activeConversation?.messages]);

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMessageContent = inputMessage;
    setInputMessage('');
    setHasStartedChat(true);

    const newMessage: Message = {
      id: Date.now().toString(),
      content: userMessageContent,
      sender: 'user',
      timestamp: new Date()
    };

    // Create new conversation if none exists
    if (!activeConversationId) {
      const newConversation: Conversation = {
        id: Date.now().toString(),
        title: userMessageContent.slice(0, 30) + (userMessageContent.length > 30 ? '...' : ''),
        messages: [newMessage],
        lastUpdated: new Date()
      };
      setConversations([newConversation]);
      setActiveConversationId(newConversation.id);
    } else {
      // Add to existing conversation
      setConversations(prev => prev.map(conv => 
        conv.id === activeConversationId 
          ? { ...conv, messages: [...conv.messages, newMessage], lastUpdated: new Date() }
          : conv
      ));
    }

    setIsTyping(true);

    // Simulate AI response
    setTimeout(() => {
      const aiResponse: Message = {
        id: (Date.now() + 1).toString(),
        content: generateAIResponse(userMessageContent),
        sender: 'ai',
        timestamp: new Date()
      };

      setConversations(prev => prev.map(conv => 
        conv.id === (activeConversationId || Date.now().toString())
          ? { ...conv, messages: [...conv.messages, aiResponse], lastUpdated: new Date() }
          : conv
      ));
      setIsTyping(false);
    }, 1500);
  };

  const generateAIResponse = (userMessage: string): string => {
    const responses = [
      "That's an interesting question! Let me think about that for you.",
      "I understand what you're asking. Here's my perspective on that topic.",
      "Great question! I'd be happy to help you explore this further.",
      "That's a thoughtful inquiry. Let me provide you with some insights.",
      "I appreciate you sharing that with me. Here's what I think about it."
    ];
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const createNewConversation = () => {
    const newConversation: Conversation = {
      id: Date.now().toString(),
      title: 'New Conversation',
      messages: [
        {
          id: Date.now().toString(),
          content: 'Hello! I\'m here to help. What would you like to discuss?',
          sender: 'ai',
          timestamp: new Date()
        }
      ],
      lastUpdated: new Date()
    };

    setConversations(prev => [newConversation, ...prev]);
    setActiveConversationId(newConversation.id);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="flex h-screen bg-white dark:bg-black">
      {/* Mobile Sidebar Overlay */}
      <AnimatePresence>
        {isSidebarOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
            onClick={() => setIsSidebarOpen(false)}
          />
        )}
      </AnimatePresence>

      {/* Sidebar */}
      <AnimatePresence>
        {isSidebarOpen && (
          <motion.div
            initial={{ x: -300 }}
            animate={{ x: 0 }}
            exit={{ x: -300 }}
            transition={{ duration: 0.3, ease: "easeInOut" }}
            className="fixed lg:relative z-50 w-80 h-full bg-gray-50 dark:bg-gray-900 border-r border-gray-200 dark:border-gray-800 flex flex-col"
          >
            {/* Sidebar Header */}
            <div className="p-6 border-b border-gray-200 dark:border-gray-700">
              <div className="flex items-center justify-between mb-6">
                <motion.h2 
                  className="text-2xl font-bold text-black dark:text-white cursor-pointer hover:opacity-70 transition-opacity duration-200"
                  onClick={() => navigate('/')}
                  whileHover={{ scale: 1.02 }}
                >
                  Soul
                </motion.h2>
                <motion.button
                  className="lg:hidden p-2 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-700 transition-colors duration-200"
                  onClick={() => setIsSidebarOpen(false)}
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                >
                  <X className="w-5 h-5" />
                </motion.button>
              </div>

              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={createNewConversation}
                className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-black dark:bg-white text-white dark:text-black rounded-lg font-medium hover:bg-gray-800 dark:hover:bg-gray-200 transition-colors duration-200"
              >
                <Plus className="w-4 h-4" />
                New Chat
              </motion.button>
            </div>

            {/* Conversations List */}
            <div className="flex-1 overflow-y-auto p-4">
              <div className="space-y-2">
                {conversations.map((conversation) => (
                  <motion.button
                    key={conversation.id}
                    whileHover={{ scale: 1.01 }}
                    whileTap={{ scale: 0.99 }}
                    onClick={() => setActiveConversationId(conversation.id)}
                    className={`w-full text-left p-3 rounded-lg transition-colors duration-200 ${
                      activeConversationId === conversation.id
                        ? 'bg-white dark:bg-gray-800 shadow-sm border border-gray-200 dark:border-gray-700'
                        : 'hover:bg-white dark:hover:bg-gray-800 hover:shadow-sm'
                    }`}
                  >
                    <div className="flex items-center gap-3">
                      <MessageCircle className="w-4 h-4 text-gray-500 dark:text-white flex-shrink-0" />
                      <div className="flex-1 min-w-0">
                        <p className="font-medium truncate text-black dark:text-white">{conversation.title}</p>
                        <p className="text-sm text-gray-500 dark:text-white truncate">
                          {conversation.messages[conversation.messages.length - 1]?.content}
                        </p>
                      </div>
                    </div>
                  </motion.button>
                ))}
              </div>
            </div>

            {/* Sidebar Footer */}
            <div className="p-4 border-t border-gray-200 dark:border-gray-700">
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => navigate('/settings')}
                className="w-full flex items-center gap-3 px-4 py-3 text-gray-600 dark:text-white hover:bg-white dark:hover:bg-gray-800 hover:text-black dark:hover:text-gray-300 rounded-lg transition-colors duration-200"
              >
                <Settings className="w-4 h-4" />
                Settings
              </motion.button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col">
        {/* Chat Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-200 dark:border-gray-800 bg-white dark:bg-black">
          <div className="flex items-center gap-3">
            <motion.button
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.9 }}
              onClick={() => setIsSidebarOpen(!isSidebarOpen)}
              className="lg:hidden p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors duration-200"
            >
              <Menu className="w-5 h-5" />
            </motion.button>
            <motion.h1 
              className="text-2xl font-bold text-black dark:text-white cursor-pointer hover:opacity-70 transition-opacity duration-200"
              onClick={() => navigate('/')}
              whileHover={{ scale: 1.02 }}
            >
              Soul
            </motion.h1>
          </div>
          <div className="flex items-center gap-2">
            <ThemeToggle />
          </div>
        </div>

        {/* Messages Area */}
        <div className="flex-1 overflow-y-auto">
          <div className="max-w-4xl mx-auto h-full">
            {!hasStartedChat ? (
              /* Empty State */
              <div className="flex items-center justify-center h-full">
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, ease: "easeOut" }}
                  className="text-center"
                >
                  <div className="bg-gray-50 dark:bg-gray-900 rounded-2xl px-8 py-6 shadow-sm border border-gray-200 dark:border-gray-800">
                    <p className="text-xl text-gray-700 dark:text-white font-medium">What can I help with?</p>
                  </div>
                </motion.div>
              </div>
            ) : (
              /* Messages */
              <div className="p-6 space-y-6">
                {activeConversation?.messages.map((message) => (
                  <motion.div
                    key={message.id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3 }}
                    className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div className={`max-w-3xl px-6 py-4 rounded-2xl ${
                      message.sender === 'user'
                        ? 'bg-black dark:bg-white text-white dark:text-black'
                        : 'bg-gray-100 dark:bg-gray-900 text-black dark:text-white border border-gray-200 dark:border-gray-800'
                    }`}>
                      <p className="whitespace-pre-wrap">{message.content}</p>
                      <p className="text-xs opacity-60 mt-2">
                        {message.timestamp.toLocaleTimeString()}
                      </p>
                    </div>
                  </motion.div>
                ))}

                {isTyping && (
                  <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="flex justify-start"
                  >
                    <div className="max-w-3xl px-6 py-4 rounded-2xl bg-gray-100 dark:bg-gray-900 border border-gray-200 dark:border-gray-800">
                      <div className="flex items-center gap-2">
                        <div className="flex space-x-1">
                          <div className="w-2 h-2 bg-gray-400 dark:bg-gray-600 rounded-full animate-bounce"></div>
                          <div className="w-2 h-2 bg-gray-400 dark:bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                          <div className="w-2 h-2 bg-gray-400 dark:bg-gray-600 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                        </div>
                        <span className="text-sm text-gray-500 dark:text-white">Soul is typing...</span>
                      </div>
                    </div>
                  </motion.div>
                )}
                <div ref={messagesEndRef} />
              </div>
            )}
          </div>
        </div>

        {/* Input Area */}
        <div className="p-6 border-t border-gray-200 dark:border-gray-800 bg-white dark:bg-black">
          <div className="max-w-4xl mx-auto">
            {/* Prompt Dropdowns */}
            <div className="flex flex-wrap gap-3 mb-4">
              <PromptDropdown
                label="Tone"
                value={selectedTone}
                onChange={setSelectedTone}
                options={[
                  { value: 'Neutral', label: 'Neutral' },
                  { value: 'Creative', label: 'Creative' },
                  { value: 'Formal', label: 'Formal' },
                  { value: 'Casual', label: 'Casual' }
                ]}
              />
              <PromptDropdown
                label="Format"
                value={selectedFormat}
                onChange={setSelectedFormat}
                options={[
                  { value: 'Conversational', label: 'Conversational' },
                  { value: 'Structured', label: 'Structured' },
                  { value: 'Bullet Points', label: 'Bullet Points' },
                  { value: 'Detailed', label: 'Detailed' }
                ]}
              />
              <PromptDropdown
                label="Creativity"
                value={selectedCreativity}
                onChange={setSelectedCreativity}
                options={[
                  { value: 'Conservative', label: 'Conservative' },
                  { value: 'Balanced', label: 'Balanced' },
                  { value: 'Creative', label: 'Creative' },
                  { value: 'Experimental', label: 'Experimental' }
                ]}
              />
            </div>

            {/* Message Input */}
            <div className="flex gap-3 items-end">
              <div className="flex-1 relative">
                <textarea
                  ref={inputRef}
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Message Soul..."
                  className="w-full px-4 py-3 border border-gray-300 dark:border-gray-700 rounded-xl bg-white dark:bg-gray-900 text-black dark:text-white placeholder-gray-500 dark:placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-black dark:focus:ring-white focus:border-transparent resize-none transition-all duration-200"
                  rows={1}
                  style={{ minHeight: '48px', maxHeight: '120px' }}
                />
              </div>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleSendMessage}
                disabled={!inputMessage.trim()}
                className="p-3 bg-black dark:bg-white text-white dark:text-black rounded-xl hover:bg-gray-800 dark:hover:bg-gray-200 disabled:opacity-50 disabled:cursor-not-allowed transition-colors duration-200 flex-shrink-0"
              >
                <Send className="w-5 h-5" />
              </motion.button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatInterface;
