import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { Brain, Sparkles, MessageCircle, Zap, Users, Shield } from 'lucide-react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const LandingPage: React.FC = () => {
  const navigate = useNavigate();

  const features = [
    {
      icon: <Brain className="w-8 h-8" />,
      title: "Intelligent Conversations",
      description: "Engage with AI that understands context and nuance"
    },
    {
      icon: <Sparkles className="w-8 h-8" />,
      title: "Creative Assistance",
      description: "Unlock your creativity with AI-powered brainstorming"
    },
    {
      icon: <MessageCircle className="w-8 h-8" />,
      title: "Natural Communication",
      description: "Chat naturally with responses that feel human"
    },
    {
      icon: <Zap className="w-8 h-8" />,
      title: "Lightning Fast",
      description: "Get instant responses to your queries and requests"
    },
    {
      icon: <Users className="w-8 h-8" />,
      title: "Personalized Experience",
      description: "AI that adapts to your unique style and preferences"
    },
    {
      icon: <Shield className="w-8 h-8" />,
      title: "Privacy First",
      description: "Your conversations are secure and private"
    }
  ];

  const testimonials = [
    {
      name: "Sarah Chen",
      role: "Creative Director",
      content: "Soul has transformed how I approach creative projects. It's like having a brilliant collaborator available 24/7."
    },
    {
      name: "Marcus Rodriguez",
      role: "Software Engineer",
      content: "The clarity and depth of Soul's responses are remarkable. It's become an essential part of my workflow."
    },
    {
      name: "Emily Watson",
      role: "Writer",
      content: "Soul doesn't just answer questionsâ€”it helps me think through complex ideas and find new perspectives."
    }
  ];

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900">
      <Header />

      {/* Hero Section */}
      <section className="pt-32 pb-20 px-4 relative overflow-hidden">
        {/* Smoke Effects */}
        <div className="smoke-left"></div>
        <div className="smoke-right"></div>

        {/* Floating Particles */}
        <div className="smoke-particle" style={{left: '15%', top: '25%', width: '8px', height: '8px', animationDelay: '0s'}}></div>
        <div className="smoke-particle" style={{left: '20%', top: '45%', width: '12px', height: '12px', animationDelay: '2s'}}></div>
        <div className="smoke-particle" style={{left: '85%', top: '35%', width: '10px', height: '10px', animationDelay: '1s'}}></div>
        <div className="smoke-particle" style={{left: '80%', top: '55%', width: '6px', height: '6px', animationDelay: '3s'}}></div>
        <div className="smoke-particle" style={{left: '10%', top: '65%', width: '14px', height: '14px', animationDelay: '4s'}}></div>
        <div className="smoke-particle" style={{left: '90%', top: '25%', width: '9px', height: '9px', animationDelay: '1.5s'}}></div>

        <motion.div 
          className="max-w-6xl mx-auto text-center relative z-10"
          animate={{ y: [0, -5, 0] }}
          transition={{ duration: 4, repeat: Infinity, ease: 'easeInOut' }}
        >
          <motion.h1 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-6xl md:text-7xl font-bold mb-6 tracking-tight"
          >
            AI with <motion.span 
              className="text-accent-blue"
              animate={{ 
                textShadow: [
                  '0 0 0px rgba(59, 195, 166, 0)',
                  '0 0 20px rgba(59, 195, 166, 0.3)',
                  '0 0 0px rgba(59, 195, 166, 0)'
                ]
              }}
              transition={{ duration: 3, repeat: Infinity, ease: 'easeInOut' }}
            >
              Soul
