import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { useEffect, useRef } from 'react';
import { Brain, Sparkles, MessageCircle, Zap, Users, Shield, ArrowRight, CheckCircle, Star } from 'lucide-react';
import Header from '../components/Header';
import Footer from '../components/Footer';

const LandingPage: React.FC = () => {
  const navigate = useNavigate();
  const videoRef = useRef<HTMLVideoElement>(null);

  // Video sound control - play sound only on initial load
  useEffect(() => {
    const video = videoRef.current;
    if (video) {
      // Ensure video plays (it starts muted for autoplay compatibility)
      video.play().then(() => {
        // Unmute for 3 seconds to let user hear the intro
        video.muted = false;
        // After 3 seconds, mute the video but keep it playing
        setTimeout(() => {
          video.muted = true;
        }, 3000);
      }).catch(console.error);
    }
  }, []);

  // Scroll-reactive parallax effect
  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const scrollPercent = scrollY / (document.documentElement.scrollHeight - window.innerHeight);

      // Update CSS custom property for parallax elements
      document.documentElement.style.setProperty('--scroll-y', `${scrollY * 0.1}px`);

      // Add subtle rotation to floating elements based on scroll
      const floatingElements = document.querySelectorAll('.float-element');
      floatingElements.forEach((element, index) => {
        const rotation = scrollPercent * 360 * (index % 2 === 0 ? 1 : -1);
        (element as HTMLElement).style.transform = `translateY(-20px) rotate(${rotation}deg)`;
      });
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const features = [
    {
      icon: <Brain className="w-6 h-6" />,
      title: "Advanced Reasoning",
      description: "Deep understanding and contextual awareness that adapts to your thinking patterns"
    },
    {
      icon: <Sparkles className="w-6 h-6" />,
      title: "Creative Intelligence",
      description: "Breakthrough ideas and innovative solutions powered by cutting-edge AI"
    },
    {
      icon: <MessageCircle className="w-6 h-6" />,
      title: "Natural Dialogue",
      description: "Seamless conversations that feel intuitive and genuinely helpful"
    },
    {
      icon: <Zap className="w-6 h-6" />,
      title: "Instant Processing",
      description: "Lightning-fast responses without compromising on quality or depth"
    }
  ];

  const steps = [
    {
      number: "01",
      title: "Connect",
      description: "Start a conversation with our intelligent AI assistant"
    },
    {
      number: "02",
      title: "Collaborate",
      description: "Work together on complex problems and creative challenges"
    },
    {
      number: "03",
      title: "Create",
      description: "Transform ideas into reality with AI-powered insights"
    }
  ];

  const testimonials = [
    {
      name: "Sarah Chen",
      role: "Product Designer",
      company: "Figma",
      content: "Soul has revolutionized my design process. It's like having a brilliant design partner who never sleeps.",
      rating: 5
    },
    {
      name: "Marcus Rodriguez",
      role: "Engineering Lead",
      company: "Stripe",
      content: "The depth of technical understanding is remarkable. Soul helps me solve complex architectural challenges.",
      rating: 5
    },
    {
      name: "Emily Watson",
      role: "Content Strategist",
      company: "Notion",
      content: "Soul doesn't just generate contentâ€”it helps me think strategically about messaging and narrative.",
      rating: 5
    }
  ];

  return (
    <div className="min-h-screen relative overflow-hidden">
      <Header />

      {/* Living Canvas Background */}
      <div className="smoke-background">
        <div className="smoke-container">
          {/* Primary Floating Blobs */}
          <div className="smoke-particle parallax-slow"></div>
          <div className="smoke-particle parallax-medium"></div>
          <div className="smoke-particle parallax-fast"></div>
          <div className="smoke-particle parallax-slow"></div>

          {/* Morphing Shapes */}
          <div className="smoke-wisp parallax-medium"></div>
          <div className="smoke-wisp parallax-fast"></div>
          <div className="smoke-wisp parallax-slow"></div>

          {/* Ambient Trails */}
          <div className="smoke-trail parallax-fast"></div>
          <div className="smoke-trail parallax-medium"></div>
          <div className="smoke-trail parallax-slow"></div>
        </div>
      </div>

      {/* Floating UI Elements */}
      <div className="fixed inset-0 pointer-events-none z-10 overflow-hidden">
        <div className="absolute top-20 left-10 w-2 h-2 bg-black/5 rounded-full float-element"></div>
        <div className="absolute top-40 right-20 w-3 h-3 bg-black/4 rounded-full float-element"></div>
        <div className="absolute bottom-40 left-20 w-1.5 h-1.5 bg-black/6 rounded-full float-element"></div>
        <div className="absolute bottom-60 right-10 w-2.5 h-2.5 bg-black/3 rounded-full float-element"></div>
        <div className="absolute top-1/3 left-1/4 w-1 h-1 bg-black/5 rounded-full float-element"></div>
        <div className="absolute top-2/3 right-1/3 w-2 h-2 bg-black/4 rounded-full float-element"></div>
      </div>

      {/* Hero Section */}
      <section className="relative pt-24 pb-32 px-6 overflow-hidden">
        <div className="max-w-7xl mx-auto">
          <div className="grid lg:grid-cols-2 gap-12 items-center">
            {/* Left side - Content */}
            <div className="lg:pr-8">
              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, ease: "easeOut" }}
                className="mb-8"
              >
                <span className="inline-flex items-center px-4 py-2 rounded-full bg-gray-50 border border-gray-200 text-sm font-medium text-gray-700 mb-8">
                  <Sparkles className="w-4 h-4 mr-2 text-gray-500" />
                  Your Intelligent Partner in Code, Thought, and Creation
                </span>
              </motion.div>

              <motion.h1 
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.1, ease: "easeOut" }}
                className="text-5xl md:text-6xl lg:text-7xl font-bold mb-8 tracking-tight text-black leading-[0.9]"
              >
                AI with
                <span className="block bg-gradient-to-r from-gray-900 to-gray-600 bg-clip-text text-transparent">
                  Soul
                </span>
              </motion.h1>

              <motion.p 
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.2, ease: "easeOut" }}
                className="text-xl md:text-2xl text-gray-600 mb-12 leading-relaxed font-light"
              >
                Experience the future of intelligent assistance. Thoughtful, creative, and remarkably human.
              </motion.p>

              <motion.div 
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.3, ease: "easeOut" }}
                className="flex flex-col sm:flex-row gap-4 mb-12"
              >
                <button 
                  onClick={() => navigate('/onboarding')}
                  className="group px-8 py-4 bg-black text-white rounded-xl font-semibold text-lg hover:bg-gray-800 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5 flex items-center gap-2"
                >
                  Start Building
                  <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-200" />
                </button>
                <button 
                  onClick={() => navigate('/about')}
                  className="px-8 py-4 bg-transparent border border-gray-300 text-gray-700 rounded-xl font-semibold text-lg hover:border-gray-400 hover:bg-gray-50 transition-all duration-300"
                >
                  Watch Demo
                </button>
              </motion.div>

              <motion.div
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.4, ease: "easeOut" }}
                className="flex items-center gap-8 text-sm text-gray-500"
              >
                <div className="flex items-center gap-2">
                  <CheckCircle className="w-4 h-4 text-green-500" />
                  No credit card required
                </div>
                <div className="flex items-center gap-2">
                  <CheckCircle className="w-4 h-4 text-green-500" />
                  Free to start
                </div>
              </motion.div>
            </div>

            {/* Right side - Video */}
            <div className="lg:pl-8 lg:scale-125">
              <motion.div
                initial={{ opacity: 0, x: 30 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.8, delay: 0.2, ease: "easeOut" }}
                className="relative"
              >
                <video
                  ref={videoRef}
                  className="w-full h-auto"
                  style={{ filter: 'invert(1)' }}
                  loop
                  autoPlay
                  muted
                  playsInline
                  preload="auto"
                >
                  <source src="/videoforbg.mp4" type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
              </motion.div>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-24 px-6 relative z-10 bg-gray-50 breathe">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-20">
            <motion.h2 
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
              className="text-4xl md:text-6xl font-bold mb-6 text-black"
            >
              Built for Excellence
            </motion.h2>
            <motion.p
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.1 }}
              viewport={{ once: true }}
              className="text-xl text-gray-600 max-w-2xl mx-auto"
            >
              Every interaction is designed to feel natural, intelligent, and genuinely helpful.
            </motion.p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
            {features.map((feature, index) => (
              <motion.div 
                key={index}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: index * 0.1 }}
                viewport={{ once: true }}
                className="group p-8 bg-white rounded-2xl border border-gray-200 hover:border-gray-300 hover:shadow-lg transition-all duration-300 h-full glow-on-hover"
              >
                <div className="w-12 h-12 bg-gray-100 rounded-xl flex items-center justify-center mb-6 group-hover:bg-gray-200 transition-colors duration-300">
                  <div className="text-gray-700">{feature.icon}</div>
                </div>
                <h3 className="text-xl font-semibold mb-4 text-black">{feature.title}</h3>
                <p className="text-gray-600 leading-relaxed">{feature.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* How it Works Section */}
      <section className="py-24 px-6 relative z-10 breathe">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-20">
            <motion.h2 
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
              className="text-4xl md:text-6xl font-bold mb-6 text-black"
            >
              How it Works
            </motion.h2>
            <motion.p
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.1 }}
              viewport={{ once: true }}
              className="text-xl text-gray-600 max-w-2xl mx-auto"
            >
              Three simple steps to unlock the power of intelligent AI assistance.
            </motion.p>
          </div>

          <div className="grid md:grid-cols-3 gap-12 lg:gap-16">
            {steps.map((step, index) => (
              <motion.div 
                key={index}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: index * 0.2 }}
                viewport={{ once: true }}
                className="text-center group glow-on-hover"
              >
                <div className="relative mb-8">
                  <div className="w-20 h-20 bg-black text-white rounded-2xl flex items-center justify-center text-2xl font-bold mx-auto group-hover:bg-gray-800 transition-colors duration-300">
                    {step.number}
                  </div>
                  {index < steps.length - 1 && (
                    <div className="hidden md:block absolute top-10 left-full w-full h-0.5 bg-gray-200 -translate-y-0.5"></div>
                  )}
                </div>
                <h3 className="text-2xl font-semibold mb-4 text-black">{step.title}</h3>
                <p className="text-gray-600 leading-relaxed">{step.description}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Testimonials Section */}
      <section className="py-24 px-6 relative z-10 bg-gray-50 breathe">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-20">
            <motion.h2 
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
              className="text-4xl md:text-6xl font-bold mb-6 text-black"
            >
              Loved by Professionals
            </motion.h2>
            <motion.p
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.1 }}
              viewport={{ once: true }}
              className="text-xl text-gray-600 max-w-2xl mx-auto"
            >
              Join thousands of creators, developers, and thinkers who trust Soul with their most important work.
            </motion.p>
          </div>

          <div className="grid md:grid-cols-3 gap-8">
            {testimonials.map((testimonial, index) => (
              <motion.div 
                key={index}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
