import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Send, Plus, Settings, HelpCircle, User, Copy, Upload, MoreHorizontal, ThumbsUp, ThumbsDown, Menu, X, FileText, Folder, ExternalLink, Sparkles } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import ThemeToggle from '../components/ThemeToggle';

interface Message {
  id: string;
  content: string;
  sender: 'user' | 'ai';
  timestamp: Date;
  isExpanded?: boolean;
}

interface UploadedFile {
  id: string;
  name: string;
  type: string;
  size: number;
}

const ChatInterface: React.FC = () => {
  const navigate = useNavigate();
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [uploadedFiles, setUploadedFiles] = useState<UploadedFile[]>([]);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [hoveredMessageId, setHoveredMessageId] = useState<string | null>(null);
  const [showFilesModal, setShowFilesModal] = useState(false);
  const [showHelpModal, setShowHelpModal] = useState(false);

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLTextAreaElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async () => {
    if (!inputMessage.trim() && uploadedFiles.length === 0) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      content: inputMessage,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setUploadedFiles([]);
    setIsTyping(true);

    // Simulate AI response
    setTimeout(() => {
      const aiResponse: Message = {
        id: (Date.now() + 1).toString(),
        content: generateAIResponse(inputMessage),
        sender: 'ai',
        timestamp: new Date()
      };
      setMessages(prev => [...prev, aiResponse]);
      setIsTyping(false);
    }, 1500);
  };

  const generateAIResponse = (userMessage: string): string => {
    const responses = [
      "I understand your question. Let me provide a comprehensive response that addresses your specific needs.",
      "That's an interesting point. Here's my analysis of the situation and some recommendations.",
      "I can help you with that. Let me break this down into clear, actionable steps.",
      "Based on your input, here are some insights and suggestions that might be helpful."
    ];
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    const newFiles: UploadedFile[] = files.map(file => ({
      id: Date.now().toString() + Math.random(),
      name: file.name,
      type: file.type,
      size: file.size
    }));
    setUploadedFiles(prev => [...prev, ...newFiles]);
  };

  const removeFile = (fileId: string) => {
    setUploadedFiles(prev => prev.filter(file => file.id !== fileId));
  };

  const copyMessage = (content: string) => {
    navigator.clipboard.writeText(content);
  };

  const toggleMessageExpansion = (messageId: string) => {
    setMessages(prev => prev.map(msg => 
      msg.id === messageId ? { ...msg, isExpanded: !msg.isExpanded } : msg
    ));
  };

  const sendFollowUp = (prompt: string) => {
    setInputMessage(prompt);
    setTimeout(() => handleSendMessage(), 100);
  };

  const handleNewChat = () => {
    setMessages([]);
    setInputMessage('');
    setUploadedFiles([]);
    setIsMobileMenuOpen(false);
  };

  const handleMyFiles = () => {
    setShowFilesModal(true);
    setIsMobileMenuOpen(false);
  };

  const handleHelp = () => {
    setShowHelpModal(true);
    setIsMobileMenuOpen(false);
  };

  const formatMessageContent = (content: string) => {
    // Simple markdown-like formatting
    return content
      .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
      .replace(/\*(.*?)\*/g, '<em>$1</em>')
      .replace(/`(.*?)`/g, '<code class="bg-gray-100 px-1 py-0.5 rounded text-sm">$1</code>');
  };

  return (
    <div className="flex flex-col h-screen bg-white dark:bg-black transition-colors duration-300">
      {/* Top Navigation */}
      <motion.header 
        initial={{ y: -20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        className="sticky top-0 z-50 bg-white/80 dark:bg-black/80 backdrop-blur-xl border-b border-gray-200 dark:border-gray-800 px-4 py-3"
      >
        <div className="max-w-4xl mx-auto flex items-center justify-between">
          {/* Left side - Logo and Navigation */}
          <div className="flex items-center gap-6">
            <div className="flex items-center gap-3">
              <motion.button 
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className="md:hidden p-2 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors"
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              >
                <Menu className="w-5 h-5 text-gray-700 dark:text-gray-300" />
              </motion.button>
              <motion.h1 
                whileHover={{ scale: 1.02 }}
                className="text-xl font-bold text-gray-900 dark:text-white cursor-pointer hover:text-gray-700 dark:hover:text-gray-300 transition-colors flex items-center gap-2" 
                onClick={() => navigate('/')}
              >
                <Sparkles className="w-5 h-5 text-purple-600 dark:text-purple-400" />
                Soul
              </motion.h1>
            </div>

            {/* Desktop Navigation */}
            <nav className="hidden md:flex items-center gap-2">
              <motion.button 
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleNewChat}
                className="flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-white hover:bg-gradient-to-r hover:from-purple-600 hover:to-blue-600 rounded-xl transition-all duration-300 shadow-sm hover:shadow-lg"
              >
                <Plus className="w-4 h-4" />
                New Chat
              </motion.button>
              <motion.button 
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleMyFiles}
                className="flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-white hover:bg-gradient-to-r hover:from-green-600 hover:to-teal-600 rounded-xl transition-all duration-300 shadow-sm hover:shadow-lg"
              >
                <Folder className="w-4 h-4" />
                My Files
              </motion.button>
              <motion.button 
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => navigate('/settings')}
                className="flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-white hover:bg-gradient-to-r hover:from-orange-600 hover:to-red-600 rounded-xl transition-all duration-300 shadow-sm hover:shadow-lg"
              >
                <Settings className="w-4 h-4" />
                Settings
              </motion.button>
              <motion.button 
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleHelp}
                className="flex items-center gap-2 px-4 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-white hover:bg-gradient-to-r hover:from-blue-600 hover:to-indigo-600 rounded-xl transition-all duration-300 shadow-sm hover:shadow-lg"
              >
                <HelpCircle className="w-4 h-4" />
                Help
              </motion.button>
            </nav>
          </div>

          {/* Right side - Theme Toggle and User Avatar */}
          <div className="flex items-center gap-3">
            <ThemeToggle />
            <motion.div 
              whileHover={{ scale: 1.1, rotate: 5 }}
              whileTap={{ scale: 0.9 }}
              className="w-8 h-8 bg-gradient-to-br from-purple-500 to-blue-600 rounded-full flex items-center justify-center hover:shadow-lg transition-all duration-300 cursor-pointer"
            >
              <User className="w-4 h-4 text-white" />
            </motion.div>
          </div>
        </div>

        {/* Mobile Menu */}
        <AnimatePresence>
          {isMobileMenuOpen && (
            <motion.div
              initial={{ opacity: 0, height: 0 }}
              animate={{ opacity: 1, height: 'auto' }}
              exit={{ opacity: 0, height: 0 }}
              className="md:hidden border-t border-gray-200 dark:border-gray-800 mt-3 pt-3"
            >
              <nav className="flex flex-col gap-2">
                <motion.button 
                  whileHover={{ x: 5 }}
                  onClick={handleNewChat}
                  className="flex items-center gap-2 px-3 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-white hover:bg-gradient-to-r hover:from-purple-600 hover:to-blue-600 rounded-lg transition-all duration-300 text-left"
                >
                  <Plus className="w-4 h-4" />
                  New Chat
                </motion.button>
                <motion.button 
                  whileHover={{ x: 5 }}
                  onClick={handleMyFiles}
                  className="flex items-center gap-2 px-3 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-white hover:bg-gradient-to-r hover:from-green-600 hover:to-teal-600 rounded-lg transition-all duration-300 text-left"
                >
                  <Folder className="w-4 h-4" />
                  My Files
                </motion.button>
                <motion.button 
                  whileHover={{ x: 5 }}
                  onClick={() => { navigate('/settings'); setIsMobileMenuOpen(false); }}
                  className="flex items-center gap-2 px-3 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-white hover:bg-gradient-to-r hover:from-orange-600 hover:to-red-600 rounded-lg transition-all duration-300 text-left"
                >
                  <Settings className="w-4 h-4" />
                  Settings
                </motion.button>
                <motion.button 
                  whileHover={{ x: 5 }}
                  onClick={handleHelp}
                  className="flex items-center gap-2 px-3 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-white hover:bg-gradient-to-r hover:from-blue-600 hover:to-indigo-600 rounded-lg transition-all duration-300 text-left"
                >
                  <HelpCircle className="w-4 h-4" />
                  Help
                </motion.button>
              </nav>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.header>

      {/* Main Chat Area */}
      <main className="flex-1 overflow-y-auto bg-gradient-to-br from-gray-50 to-white dark:from-gray-900 dark:to-black">
        <div className="max-w-4xl mx-auto px-4 py-6">
          {messages.length === 0 ? (
            /* Empty State */
            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              className="flex items-center justify-center h-full min-h-[60vh]"
            >
              <div className="text-center">
                <motion.div
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ delay: 0.2, type: "spring", stiffness: 200 }}
                  className="w-16 h-16 bg-gradient-to-br from-purple-500 to-blue-600 rounded-2xl flex items-center justify-center mx-auto mb-6 shadow-lg"
                >
                  <Sparkles className="w-8 h-8 text-white" />
                </motion.div>
                <motion.h2 
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.4 }}
                  className="text-3xl font-bold text-gray-900 dark:text-white mb-3 bg-gradient-to-r from-purple-600 to-blue-600 bg-clip-text text-transparent"
                >
                  How can I help you today?
                </motion.h2>
                <motion.p 
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.6 }}
                  className="text-gray-600 dark:text-gray-400 text-lg"
                >
                  Start a conversation by typing a message below.
                </motion.p>
              </div>
            </motion.div>
          ) : (
            /* Messages */
            <div className="space-y-6">
              {messages.map((message) => (
                <motion.div
                  key={message.id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.3 }}
                  className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
                  onMouseEnter={() => setHoveredMessageId(message.id)}
                  onMouseLeave={() => setHoveredMessageId(null)}
                >
                  <div className={`max-w-3xl w-full ${
                    message.sender === 'user' 
                      ? 'bg-gradient-to-br from-purple-100 to-blue-100 dark:from-purple-900/30 dark:to-blue-900/30 rounded-2xl p-4 ml-12 border border-purple-200 dark:border-purple-700/50' 
                      : 'bg-white dark:bg-gray-800/50 border border-gray-200 dark:border-gray-700 rounded-2xl p-4 mr-12 shadow-lg dark:shadow-2xl backdrop-blur-sm'
                  } relative group hover:shadow-xl transition-all duration-300`}>
                    {/* Message Header */}
                    <div className="flex items-start gap-3 mb-2">
                      <motion.div 
                        whileHover={{ scale: 1.1 }}
                        className={`w-6 h-6 rounded-full flex items-center justify-center flex-shrink-0 ${
                        message.sender === 'user' ? 'bg-gradient-to-br from-purple-500 to-blue-600' : 'bg-gradient-to-br from-gray-800 to-black dark:from-gray-600 dark:to-gray-800'
                      } shadow-md`}>
                        {message.sender === 'user' ? (
                          <User className="w-3 h-3 text-white" />
                        ) : (
                          <Sparkles className="w-3 h-3 text-white" />
                        )}
                      </motion.div>
                      <div className="flex-1">
                        <div className="font-medium text-sm text-gray-900 dark:text-white mb-1">
                          {message.sender === 'user' ? 'You' : 'Soul'}
                        </div>
                      </div>

                      {/* Message Controls */}
                      <AnimatePresence>
                        {hoveredMessageId === message.id && (
                          <motion.div
                            initial={{ opacity: 0, scale: 0.8 }}
                            animate={{ opacity: 1, scale: 1 }}
                            exit={{ opacity: 0, scale: 0.8 }}
                            className="flex items-center gap-1"
                          >
                            <button
                              onClick={() => copyMessage(message.content)}
                              className="p-1.5 hover:bg-gray-100 rounded-lg transition-colors"
                              title="Copy message"
                            >
                              <Copy className="w-3 h-3 text-gray-500" />
                            </button>
                            {message.sender === 'ai' && (
                              <>
                                <button className="p-1.5 hover:bg-gray-100 rounded-lg transition-colors" title="Good response">
                                  <ThumbsUp className="w-3 h-3 text-gray-500" />
                                </button>
                                <button className="p-1.5 hover:bg-gray-100 rounded-lg transition-colors" title="Poor response">
                                  <ThumbsDown className="w-3 h-3 text-gray-500" />
                                </button>
                              </>
                            )}
                          </motion.div>
                        )}
                      </AnimatePresence>
                    </div>

                    {/* Message Content */}
                    <div className="prose prose-sm max-w-none">
                      <div 
                        className="text-gray-900 dark:text-gray-100 leading-relaxed"
                        dangerouslySetInnerHTML={{ __html: formatMessageContent(message.content) }}
                      />
                    </div>

                    {/* AI Response Actions */}
                    {message.sender === 'ai' && (
                      <div className="flex flex-wrap gap-2 mt-4 pt-3 border-t border-gray-100 dark:border-gray-700">
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => sendFollowUp('Explain this in simpler terms')}
                          className="px-3 py-1.5 text-xs font-medium text-gray-700 dark:text-gray-300 bg-gray-100 dark:bg-gray-700 hover:bg-gradient-to-r hover:from-blue-500 hover:to-purple-500 hover:text-white rounded-full transition-all duration-300 shadow-sm hover:shadow-md"
                        >
                          Explain simpler
                        </motion.button>
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => sendFollowUp('Provide more details about this')}
                          className="px-3 py-1.5 text-xs font-medium text-gray-700 dark:text-gray-300 bg-gray-100 dark:bg-gray-700 hover:bg-gradient-to-r hover:from-green-500 hover:to-teal-500 hover:text-white rounded-full transition-all duration-300 shadow-sm hover:shadow-md"
                        >
                          More details
                        </motion.button>
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => sendFollowUp('Try a different approach')}
                          className="px-3 py-1.5 text-xs font-medium text-gray-700 dark:text-gray-300 bg-gray-100 dark:bg-gray-700 hover:bg-gradient-to-r hover:from-orange-500 hover:to-red-500 hover:text-white rounded-full transition-all duration-300 shadow-sm hover:shadow-md"
                        >
                          Try another version
                        </motion.button>
                      </div>
                    )}
                  </div>
                </motion.div>
              ))}

              {/* Typing Indicator */}
              {isTyping && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="flex justify-start"
                >
                  <div className="max-w-3xl w-full bg-white dark:bg-gray-800/50 border border-gray-200 dark:border-gray-700 rounded-2xl p-4 mr-12 shadow-lg dark:shadow-2xl backdrop-blur-sm">
                    <div className="flex items-start gap-3">
                      <motion.div 
                        animate={{ rotate: 360 }}
                        transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
                        className="w-6 h-6 bg-gradient-to-br from-gray-800 to-black dark:from-gray-600 dark:to-gray-800 rounded-full flex items-center justify-center flex-shrink-0 shadow-md"
                      >
                        <Sparkles className="w-3 h-3 text-white" />
                      </motion.div>
                      <div className="flex-1">
                        <div className="font-medium text-sm text-gray-900 dark:text-white mb-2">Soul</div>
                        <div className="flex items-center gap-1">
                          <div className="flex space-x-1">
                            <motion.div 
                              animate={{ y: [-2, 2, -2] }}
                              transition={{ duration: 0.6, repeat: Infinity }}
                              className="w-2 h-2 bg-purple-500 rounded-full"
                            ></motion.div>
                            <motion.div 
                              animate={{ y: [-2, 2, -2] }}
                              transition={{ duration: 0.6, repeat: Infinity, delay: 0.1 }}
                              className="w-2 h-2 bg-blue-500 rounded-full"
                            ></motion.div>
                            <motion.div 
                              animate={{ y: [-2, 2, -2] }}
                              transition={{ duration: 0.6, repeat: Infinity, delay: 0.2 }}
                              className="w-2 h-2 bg-indigo-500 rounded-full"
                            ></motion.div>
                          </div>
                          <span className="text-sm text-gray-500 dark:text-gray-400 ml-2">Thinking...</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </motion.div>
              )}

              <div ref={messagesEndRef} />
            </div>
          )}
        </div>
      </main>

      {/* Input Area */}
      <footer className="sticky bottom-0 bg-white border-t border-gray-200 px-4 py-4">
        <div className="max-w-4xl mx-auto">
          {/* Uploaded Files */}
          {uploadedFiles.length > 0 && (
            <div className="flex flex-wrap gap-2 mb-3">
              {uploadedFiles.map((file) => (
                <div key={file.id} className="flex items-center gap-2 bg-gray-100 rounded-full px-3 py-1.5 text-sm">
                  <span className="text-gray-700 truncate max-w-32">{file.name}</span>
                  <button
                    onClick={() => removeFile(file.id)}
                    className="text-gray-500 hover:text-gray-700 transition-colors"
                  >
                    <X className="w-3 h-3" />
                  </button>
                </div>
              ))}
            </div>
          )}

          {/* Input Container */}
          <div className="relative">
            <div className="flex items-end gap-3 bg-gray-50 rounded-2xl border border-gray-200 focus-within:border-gray-300 transition-colors">
              {/* Upload Button */}
              <button
                onClick={() => fileInputRef.current?.click()}
                className="p-3 text-gray-500 hover:text-gray-700 transition-colors"
                title="Upload file"
              >
                <Upload className="w-5 h-5" />
              </button>

              {/* Text Input */}
              <textarea
                ref={inputRef}
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Ask Claude anything..."
                className="flex-1 bg-transparent border-none outline-none resize-none py-3 text-gray-900 placeholder-gray-500 min-h-[24px] max-h-[144px]"
                rows={1}
                style={{
                  height: 'auto',
                  minHeight: '24px',
                  maxHeight: '144px'
                }}
                onInput={(e) => {
                  const target = e.target as HTMLTextAreaElement;
                  target.style.height = 'auto';
                  target.style.height = Math.min(target.scrollHeight, 144) + 'px';
                }}
              />

              {/* Send Button */}
              <button
                onClick={handleSendMessage}
                disabled={!inputMessage.trim() && uploadedFiles.length === 0}
                className={`p-3 rounded-xl transition-all duration-200 ${
                  inputMessage.trim() || uploadedFiles.length > 0
                    ? 'bg-black text-white hover:bg-gray-800 hover:scale-105'
                    : 'bg-gray-200 text-gray-400 cursor-not-allowed'
                }`}
              >
                <Send className="w-5 h-5" />
              </button>
            </div>

            {/* File Input */}
            <input
              ref={fileInputRef}
              type="file"
              multiple
              accept=".pdf,.txt,.doc,.docx,.png,.jpg,.jpeg,.gif"
              onChange={handleFileUpload}
              className="hidden"
            />
          </div>

          {/* Input Helper Text */}
          <div className="text-xs text-gray-500 mt-2 text-center">
            Press Enter to send, Shift + Enter for new line
          </div>
        </div>
      </footer>
    </div>
  );
};

export default ChatInterface;
